/**
 * Get a random conversation starter
 */
export const getRandomConversationStarter = (): string => {
  return conversationStarters[Math.floor(Math.random() * conversationStarters.length)];
};

/**
 * Get a random situation topic
 */
export const getRandomSituationTopic = (): string => {
  return situationTopics[Math.floor(Math.random() * situationTopics.length)];
};

/**
 * Get a random topic lesson
 */
export const getRandomTopicLesson = (): string => {
  return topicLessons[Math.floor(Math.random() * topicLessons.length)];
};

/**
 * Get a random topic for free conversation (randomly chooses between conversation starters, topic lessons, or deep questions)
 */
export const getRandomFreeConversationTopic = (): string => {
  const randomChoice = Math.random();
  if (randomChoice < 0.33) {
    return getRandomConversationStarter();
  } else if (randomChoice < 0.66) {
    return getRandomTopicLesson();
  } else {
    return getRandomDeepQuestion();
  }
};

/**
 * Get a random interview topic (randomly chooses between conversation starters or deep questions)
 */
export const getRandomInterviewTopic = (): string => {
  const useConversationStarter = Math.random() < 0.5;
  return useConversationStarter ? getRandomConversationStarter() : getRandomDeepQuestion();
};

/**
 * Get a random topic based on conversation mode
 */
export const getRandomTopicForMode = (mode: string): string => {
  switch (mode) {
    case 'interview':
      return getRandomInterviewTopic();
    case 'topic_lesson':
      return getRandomTopicLesson();
    case 'free_conversation':
      return getRandomFreeConversationTopic();
    case 'situation_simulation':
      return getRandomSituationTopic();
    default:
      return getRandomConversationStarter();
  }
};
